import React from 'react';
import { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'reactstrap';


const Botoncillo = (props) => {
  <Button onClick={ ()=>props.funci } color={ props.color } >
    props.numero
  </Button>
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      color: "secundary",
      numero: 0,
    }
  }

  sumar(){
    this.setState({ numero:this.state.numero++ });
  }

  color(){
    if (this.state.numero>0) {
      this.setState({ color:"danger" })
    }
  }


  render() {
    return (
      <div className="App">
        <header className="App-header">
          <Botoncillo numero={this.state.numero} funci={()=>this.sumar} color={ ()=>this.color() } />
        </header>
      </div>
    );
  }
}

export default App;
