import React from 'react';
import { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'reactstrap';


const Botoncillo = (props) => {
  return (
    <Button onClick={() => props.funci()} color={props.color}>
      {props.numero}
    </Button>
  );
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      color: "secondary",
      numero1: 0,
      numero2: 0,
      numero3: 0,
      numero4: 0,
      numero5: 0,
    }
  }

  sumar1() {
    this.setState({ numero1: this.state.numero1+1 });
  }
  sumar2() {
    this.setState({ numero2: this.state.numero2+1 });
  }
  sumar3() {
    this.setState({ numero3: this.state.numero3+1 });
  }
  sumar4() {
    this.setState({ numero4: this.state.numero4+1 });
  }
  sumar5() {
    this.setState({ numero: this.state.numero5+1 });
  }

  color() {
    if (this.state.numero > 0) {
      this.setState({ color: "danger" })
    } else {
      this.setState({ color: "secondary" })
    }
  }


  render() {
    return (
      <div className="App">
        <header className="App-header">

          <Botoncillo numero={this.state.numero1} funci={() => this.sumar1()} color={this.state.color} />
          {" "}
          <Botoncillo numero={this.state.numero2} funci={() => this.sumar2()} color={this.state.color} />
          {" "}
          <Botoncillo numero={this.state.numero3} funci={() => this.sumar3()} color={this.state.color}/>
          {" "}
          <Botoncillo numero={this.state.numero4} funci={() => this.sumar4()} color={this.state.color} />
          {" "}
          <Botoncillo numero={this.state.numero5} funci={() => this.sumar5()} color={this.state.color}/>
        </header>
      </div>
    );
  }
}

export default App;
